'use strict';

const env = require('mquery').env;

console.log('environment: %s', env.type);

let col;
switch (env.type) {
  case 'node':
    col = require('mquery/test/collection/node');
    break;
  case 'mongo':
    col = require('mquery/test/collection/mongo');
    break;
  case 'browser':
    col = require('mquery/test/collection/browser');
    break;
  default:
    throw new Error('missing collection implementation for environment: ' + env.type);
}

module.exports = exports = col;
